import React, { useEffect, useState } from 'react'

const Actions = ({postId, postLikes, is_liked}) => {

  const [like, setLike] = useState(0);
  const [liked, setLiked] = useState(is_liked);
  const [shouldSend, setShouldSend] = useState(false);


    useEffect(()=>{
    async function sendLikes(){
    try{
    const response = await fetch('http://127.0.0.1:8000/posts/update/likes/',{
    method:'POST',
    headers: {'Content-type':'application/json'},
    body: JSON.stringify({
      post_id : postId,
      likes : like,
      liked : liked
    })
  })
  const data = await response.json();
    if(response.status === 200) {
      console.log("liked updated successfully",data);
    }
    else{
      console.log("liked not updated successfully",data)

    }
  }
  catch(error){
    console.error("Error while updating likes",error)
  }    
}
if (shouldSend) {
  sendLikes();
  setShouldSend(false);
}

},[like, postId, shouldSend])


useEffect(() => {
  // Fetch post data to determine if the user has liked the post
  async function fetchPostData() {
    const response = await fetch(`/api/posts/${postId}/`);
    const data = await response.json();

    console.log(data);
    // setLiked(data.user_liked);
    // setLike(data.likes_count);
  }
  fetchPostData();
}, [postId]);

function handleLike(){
  if(!liked){
  setLike(like+1);
  setLiked(true);
  }
  else{
  setLike(like-1);
  setLiked(false);
  }
  setShouldSend(true);
}


    return (
      <>
