const Actions = ({postId,postLikes,user_id,likes}) => {

  const [like, setLike] = useState(postLikes);
  const [usersLiked, setUsersLiked] = useState(likes);

  useEffect(()=>{
  const sendLikes = async (like) => {
    try {
      const response = await fetch('http://127.0.0.1:8000/posts/update/likes/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          post_id: postId,
          likes_count: like,
        }),
      });

      const data = await response.json();
      if (response.status === 200) {
        console.log('Like updated successfully', data);
        fetchPostData(); // Fetch updated likes after successful like update
