  const [like, setLike] = useState(0);
  const [liked, setLiked] = useState(false);

  function handleLike(){
    if(!liked){
    setLike(like+1);
    setLiked(true);
    }
    else{
    setLike(like-1);
    setLiked(false);
    }

  }
    return (
      <>
      <button className = "reaction-button" onClick={handleLike}>
      {liked ? 
