    const response = await fetch('http://127.0.0.1:8000/posts/update/likes/',{
    method:'POST',
    headers: {'Content-type':'application/json'},
    body: JSON.stringify({
      post_id : postId,
      likes : like
    })
  })
  const data = await response.json();
    if(response.status === 200) {
      console.log("liked updated successfully",data);
    }
    else{
      console.log("liked not updated successfully",data)
    }
  }
  catch(error){
    console.error("Error while updating likes",error)
  }
