    try{
    const response = await fetch('http://127.0.0.1:8000/posts/update/likes/',{
    method:'POST',
    headers: {'Content-type':'application/json'},
    body: JSON.stringify({
      post_id : postId,
      likes : like
    })
  })
    if(response.ok) {
      console.log("liked updated successfully");
    }
    else{
      console.log()
    }
  }
  catch(e){
    console.error("E")
  }
}
